create database website_book_lib;
use website_book_lib;
create table auth(
	id int not null auto_increment,
    name nvarchar(20) not null,
    account nvarchar(20) not null,
    passwd nvarchar(20) not null,
    is_connect nvarchar(2) not null,
    primary key(id)
);
create table book(
	id int not null auto_increment,
    title nvarchar(100) not null,
    auth nvarchar(100) not null,
    description mediumtext,
    update_day Date not null,
    num_of_pages int not null,
    category nvarchar(100) not null,
    img longtext,
    primary key(id)
);

create table container(
	id int not null auto_increment,
    idd_customer int not null,
    idd_book int not null,
    amount int not null,
    primary key(id)
);

create table comment(
	id int not null auto_increment,
    id_customer int not null,
    id_book int not null,
    cmt mediumtext,
    star int not null,
    primary key(id)
);

